import { Led } from 'johnny-five';
import config from 'config';
import Microphone from '../microphone';
import Translator from '../translator';
import Light from '../light';
import Motor from '../motor';
import Speaker from '../speaker';
import Infrared from '../infrared';
import logUtil from '../../utils/logUtil';
import { GENERAL_CONSTANTS } from '../../constants';
const {
	PAUSE_TIME_BETWEEN_INTERACTIONS
} = GENERAL_CONSTANTS;

/**
 * @class Box is the class that contains the methods for all actions a Box
 * should be able to perform. It takes care of orchestring the chain of
 * events.
 */
export default class Box {
	/**
	 * Constructor for the Light class
	 * @param {JohnnyFive board instanxe} board - this is the board class delivered
	 * by johnny five for one board. In this case we talk about a photon board
	 * @param {Mqtt client} client - This is an  instance of an mqtt client that
	 * will be used to communicate with oder boxes
	 * @param {Object} options - Option containing next, prev and isMaster
	 */
	constructor(board, client, options) {
		/**
		 * A Johnny Five Board instance
		 * @type {Board}
		 */
		this.board = board;
		/**
		 * An Mqtt client
		 * @type {Mqtt client}
		 */
		this.client = client;
		/**
		 * Option with next, prev and isMaster
		 * @type {Object}
		 */
		this.options = options;
		/**
		 * The Box's name
		 * @type {String}
		 */
		this.name = this.board.id;
		/**
		 * The round actually playing
		 * @type {Number}
		 */
		this.round = 0;

		// initialise subcomponents
		/**
		 * The Translator component
		 * Will be intialized when all boards are ready
		 * @type {[type]}
		 */
		this.translator = null;
		/**
		 * The Light component
		 * Will be intialized when all boards are ready
		 * @type {Light}
		 */
		this.light = null;
		/**
		 * The Motor component
		 * Will be intialized when all boards are ready
		 * @type {Motor}
		 */
		this.motor = null;
		/**
		 * The Speak component
		 * Will be intialized when all boards are ready
		 * @type {Speak}
		 */
		this.speaker = null;
		/**
		 * The Infra component
		 * Will be intialized when all boards are ready
		 * @type {Infra}
		 */
		this.infrared = null;
		/**
		 * The Micro component
		 * Will be intialized when all boards are ready
		 * @type {Micro}
		 */
		this.microphone = null;

		this.testMotor = this.testMotor.bind(this);

		client.on('connect', this.onConnect.bind(this));
		client.on('message', this.onMessage.bind(this));
	}
	/** Is called when the Box's mqtt client is ready and connected. */
	onConnect() {
		logUtil.log({
			type: 'shiftr',
			title: `Box "${ this.name }" connected and subscribed`,
			messages: [ { 'subscription topic': `/inputs/${ this.name }` } ]
		});
		this.client.subscribe(`/inputs/${ this.name }`);
		this.board.on('ready', this.onBoardReady.bind(this));
	}
	/**
	 * Is called when a mqtt message is received
	 * @param  {string} topic - the mqtt topic the message was sent on
	 * @param  {string} message - the message sent via mqtt
	 */
	onMessage(topic, message) {
		clearTimeout(this.interval);
		logUtil.log({
			type: 'shiftr',
			title: `Box "${ this.name }" received a message`,
			messages: [
				{ topic },
				{ message: message.toString() }
			]
		});

		switch (message.toString()) {
			case 'done':
				this.round++;
				this.prepareToSpeak.bind(this)();
				break;
			case 'readyToListen':
				this.speakText.bind(this)();
				break;
			case 'readyToSpeak':
				this.onPreviousBoxSpeaking.bind(this)();
				break;
			default: return;
		}
	}
	/**
	 * Send a message to wether the nex or the previous Box via mqtt
	 * @param  {string} message - message to be sent to the sibling Box
	 * @param  {string} recipient - wether "next" or "prev"
	 */
	sendMessage(message, recipient = 'next') {
		const recipientBox = this.options[ recipient ];
		if (!recipientBox) {
			logUtil.log({
				type: 'warning',
				title: `Box "${ this.name }" has an invalid next sibling`,
				messages: [
					{ sibling: recipientBox.name }
				]
			});
		}

		const topic = `/inputs/${ recipientBox.name }`;
		logUtil.log({
			type: 'shiftr',
			title: `Box "${ this.name }" forwarded a message`,
			messages: [
				{ from: this.name },
				{ to: recipientBox.name },
				{ topic },
				{ message }
			]
		});
		this.client.publish(topic, message);
	}
	/** Is called when the Photon board is ready */
	onBoardReady() {
		this.translator = new Translator(this.name, this.options.language);
		this.speaker = new Speaker(this.name, this.options.language);
		this.microphone = new Microphone(this.name, this.options.language);
		this.motor = new Motor(this.board);
		this.light = new Light(this.board);
		this.infrared = new Infrared(this.board);
		logUtil.log({
			type: 'hardware',
			title: `Box's board of "${ this.name }" is ready`
		});
	}
	/**
	 * Retruns the Box's mqtt client
	 * @return {Mqtt client instance}
	 */
	getClient() {
		return this.client;
	}
	/**
	 * Returns the Box's j5 board
	 * @return {Johnny Five board instance}
	 */
	getBoard() {
		return this.board;
	}
	/** Internal start, starts detecting */
	start() {
		this.motor.lieDown()
			.then(() => {
				logUtil.log({
					type: 'hardware',
					title: `Box's "${ this.name }" starts detecting`
				});
				this.infrared.detectPresence()
					.then(this.onPresenceDetected.bind(this));
			});
	}
	/** External start, intreases the round */
	startTheShow() {
		this.round++;
		logUtil.log({
			type: 'info',
			title: `Box "${ this.name }" starts the show`,
			messages: [ { round: this.round } ]
		});
		this.start.bind(this)();
	}
	/** Restarts the complete show and resets the round */
	restartTheShow() {
		this.round = 1;
		this.start.bind(this)();
	}
	/** Is called when the infrared detects a presence */
	onPresenceDetected() {
		logUtil.log({
			type: 'info',
			title: `Box "${ this.name }" detected a presence`
		});
		this.motor.standUp()
			.then(() => {
				this.motor.lookUp()
					.then(() => {
						this.speaker.explainRules()
							.then(this.onRulesExplained.bind(this))
							.catch(() => {
								logUtil.log({
									type: 'error',
									title: `Rules not explained`
								});
								this.motor.lookStraight()
									.then(this.startTheShow.bind(this));
							});
					});
			});
	}
	/** Is called when the Box has spoken the rules out loud */
	onRulesExplained() {
		logUtil.log({
			type: 'info',
			title: `Box "${ this.name }" explained the rules`
		});
		this.motor.lookStraight()
			.then(() => {
				this.light.startBlinking();
				this.microphone.startRecording()
					.then(() => {
						logUtil.log({
							type: 'info',
							title: `Box "${ this.name }" has successfully recorded a voice`
						});
						this.prepareToSpeak.bind(this)();
					})
					.catch(this.onRecordingFailed.bind(this));
			});
	}
	/** Is called when the Box is meant to speak */
	prepareToSpeak() {
		this.light.stopBlinking()
			.then(() => {
				const isLastRound = this.isLastRound.bind(this)();
				if (isLastRound) {
					this.speakText.bind(this)();
				} else {
					this.sendMessage.bind(this)('readyToSpeak');
				}
				logUtil.log({
					type: 'info',
					title: `Box "${ this.name }" is prepared to speak`,
					messages: [ { round: this.round } ]
				});
			});
	}
	/** Speaks the text out loud */
	speakText() {
		this.motor.lookUp()
			.then(() => {
				this.speaker.speakText()
					.then(this.onTextSpoken.bind(this))
					.catch((err) => {
						logUtil.log({
							type: 'warning',
							title: `Box "${ this.name }" failed to repeat the text`,
							messages: [ { then: 'Retries on time again' } ]
						});
						this.motor.lookStraight()
							.then(() => {
								this.speaker.sayNoRecordingError()
									.then(this.startTheShow.bind(this));
							});
					});
			});
	}
	/**
	 * Is called when the voice was badly recorded, when no sound was identified
	 * or when the speech could not be transformed into text
	 */
	onRecordingFailed() {
		this.light.stopBlinking()
			.then(() => {
				logUtil.log({
					type: 'warning',
					title: `Box "${ this.name }" failed to record the voice`,
					messages: [ { then: 'Starts over again' } ]
				});
				this.motor.lookUp()
					.then(() => {
						this.speaker.sayNoRecordingError()
							.then(this.startTheShow.bind(this));
					});
			});
	}
	/** Is called whe the box has successfully spoken the text out loud */
	onTextSpoken() {
		logUtil.log({
			type: 'info',
			title: `Box "${ this.name }" repeated the text`
		});
		this.motor.lookStraight()
			.then(() => {
				this.motor.lieDown()
					.then(this.handleTranslation.bind(this));
			});
	}
	/** Ends the show of the box */
	handleTranslation() {
		if (this.options.isMaster) {
			if (this.round === 1) {
				logUtil.log({
					type: 'info',
					title: `Box "${ this.name }"s is now translating`,
					messages: [
						{ next: this.options.next.name },
						{ 'from language': this.options.language },
						{ 'to language': this.options.next.language }
					]
				});
				this.translator.translateNext(this.options.next)
					.then(this.finish.bind(this));
			} else {
				logUtil.log({
					type: 'info',
					title: `Box "${ this.name }"s show is over`,
					messages: [ { then: 'Starts over again' } ]
				});
				this.round = 0;
				this.startTheShow.bind(this)();
			}
		} else {
			this.translator.translateNext(this.options.next)
				.then(this.finish.bind(this));
		}
	}
	/** Is called when the box finished its round */
	finish() {
		logUtil.log({
			type: 'info',
			title: `Box "${ this.name }" finished its round`,
			messages: [ { round: this.round } ]
		});
		/** The component's timeout. Saved to be cleared at any time */
		this.timeout = setTimeout(() => {
			this.sendMessage.bind(this)('done');
			logUtil.log({
				type: 'info',
				title: `Box "${ this.name }"s show is over`
			});
		}, PAUSE_TIME_BETWEEN_INTERACTIONS);
	}
	/** Is called when the previous box is speaking the text out loud */
	onPreviousBoxSpeaking() {
		logUtil.log({
			type: 'info',
			title: `Box "${ this.name }" is listening to its previous sibling`
		});
		this.motor.standUp()
			.then(() => {
				this.light.startBlinking();
				this.motor.lookStraight()
					.then(() => {
						this.sendMessage.bind(this)('readyToListen', 'prev');
					});
			});
	}
	/** Is called to check is this is the last round */
	isLastRound() {
		return this.options.isMaster && this.round > 1;
	}
	/** A method to reinitialize the box to a standing state */
	allStandUp() {
		this.motor.standUp();
		this.motor.lookStraight();
	}
	/** A method to reinitialize the box to the initial state */
	reset() {
		this.motor.lieDown();
		this.motor.lookStraight();
		this.light.stopBlinking();
	}
	/** A method to test that all components are connected and working correctly */
	testMotor() {
		return new Promise((resolve, reject) => {
			this.delayedCall.bind(this)(this.motor.standUp)
				.then(() => {
					this.delayedCall.bind(this)(this.motor.lookUp)
						.then(() => {
							this.delayedCall.bind(this)(this.motor.lookStraight)
								.then(() => {
									this.delayedCall.bind(this)(this.motor.lieDown)
										.then(resolve);
								});
						});
				});
		});
	}
	/** A utility to delay an action returning a Promise */
	delayedCall(callback) {
		return new Promise((resolve) => {
			/** The component's timeout. Saved to be cleared at any time */
			this.timeout = setTimeout(() => {
				callback();
				clearTimeout(this.timeout);
				resolve();
			}, 1000);
		});
	}
}

import _ from 'lodash';
import { Proximity } from 'johnny-five';
import logUtil from '../../utils/logUtil';
import { INFRARED_CONSTANTS } from '../../constants';
const {
	UNUSED_PIN,
	USED_PIN,
	MIN_DETECTION_DISTANCE,
	CONTROLLER_ID,
	MIN_PRESENCE_DURATION,
	EMPTY_RESOLVE_FUNCTION
} = INFRARED_CONSTANTS;

/**
 * @class Manages the infrared sensor
 */
export default class Infrared {
	/**
	 * Constructor for the Infrared class. Needs a j5 board instance
	 * to work well. !CAUTION¡ The j5 board must already be ready.
	 * @param  {Johnny Five board instance} board
	 * @private
	 */
	constructor(board) {
		/**
		 * Johnny Five Board instance
		 * @type {Board}
		 */
		this.board = board;
		/**
		 * Johnny Five Proximity instance
		 * @type {[type]}
		 */
		this.proximity = this.getInitializedProximity.bind(this)();
		/**
		 * Actual detected distance in cm
		 * @type {Number}
		 */
		this.distance = 0;
		/**
		 * Wether or not a human presence is detected
		 * @type {Boolean}
		 */
		this.presenceDetected = false;
		/**
		 * A throttled function checking if a human presence
		 * is still detected after a certain time
		 * @type {Throttled Function}
		 */
		this.throttledCheck = _.throttle(
			this.checkLaterIfStillPresent.bind(this),
			MIN_PRESENCE_DURATION
		);
	}
	/**
	 * Is called when the infrared detects a distance. In other words,
	 * all the time.
	 * @param  {Number} cm
	 * @private
	 */
	onData(cm) {
		if (cm < MIN_DETECTION_DISTANCE) {
			this.presenceDetected = true;
			logUtil.log({
				type: 'hardware',
				title: 'Presence',
				messages: [ { distance: `${cm} cm` } ]
			});
			this.throttledCheck();
		} else {
			this.presenceDetected = false;
		}
	}
	/**
	 * @return {Johnny Five Proximity class} Return a valid Johnny Five
	 * instance of the Proximity class. See http://johnny-five.io/api/proximity/
	 * for mor details.
	 * @private
	 */
	getInitializedProximity() {
		const unused = new Proximity({
			controller: CONTROLLER_ID,
			pin: UNUSED_PIN,
			board: this.board
		});
		const proximity = new Proximity({
			controller: CONTROLLER_ID,
			pin: USED_PIN,
			board: this.board
		});
		return proximity;
	}
	/**
	 * Is called to start the infrared detection. Overrides the global
	 * resolve function with the Promise.resolve function.
	 * @return {Promise}
	 * @public
	 */
	detectPresence() {
		return new Promise((resolve) => {
			this.proximity.on('data', (data) => {
				this.onData.bind(this)(data.cm);
			});
			/**
			 * Resolve function to be triggered when a remaining presence
			 * is detected
			 * @type {Promise.resolve}
			 */
			this.resolve = resolve;
		});
	}
	/**
	 * Checks if a presence is detected and resovles the Promise
	 * if so. Resets the promise to its default.
	 * @private
	 */
	checkLaterIfStillPresent() {
		clearTimeout(this.timeout);
		/**
		 * Timeout for rechecking if the presence is remaining
		 * after a certain amount of time
		 */
		this.timeout = setTimeout(() => {
			if (this.presenceDetected) {
				logUtil.log({
					type: 'hardware',
					title: 'Long presence detected'
				});
				this.resolve();
				this.proximity.removeAllListeners('data');
			}
		}, MIN_PRESENCE_DURATION);
	}
}

import { Led } from 'johnny-five';
import { LIGHT_CONSTANTS } from '../../constants';
const { BLINK_SPEED, PIN } = LIGHT_CONSTANTS;

/**
 * @class Manages the led lights
 */
export default class Light {
	/**
	 * Constructor for the Light class. Needs a j5 board instance
	 * to work well. !CAUTION¡ The j5 board must already be ready.
	 * @param  {Johnny Five board instance} board
	 * @private
	 */
	constructor(board) {
		/**
		 * The led light of the class (for now just one)
		 * @type {Led}
		 */
		this.led = new Led({ pin: PIN, board });
	}
	/**
	 * Is called to make the lights blink
	 * @return {Promise}
	 * @public
	 */
	startBlinking() {
		return new Promise((resolve) => {
			this.led.blink(BLINK_SPEED);
			resolve();
		});
	}
	/**
	 * Is called to make the lights stop blinking
	 * @return {Promise}
	 * @public
	 */
	stopBlinking() {
		return new Promise((resolve) => {
			this.led.stop().off();
			resolve();
		});
	}
}

import config from 'config';
import fs from 'fs';
import Record from './records';
import Report from './reports';
import SpeechToText from './speechtotext';
import logUtil from '../../utils/logUtil';

/**
 * A class managing the audio recording and turning soud into text
 */
export default class Microphone {
	/**
	 * Class's constructor. Is called when initialized with the "new" keyword.
	 * @param  {String} name     - Name of the box. Used to save the audio by Box name
	 * @param  {String} language - Language of the box. Used for speeching
	 *                             and translating it later on
	 * @return {[type]}          [description]
	 */
	constructor(name, language) {
		/**
		 * Name of the box. Used to save the audio by Box name
		 * @type {String}
		 */
		this.name = name;
		/**
		 * Language of the box. Used for speeching
		 * and translating it later on
		 * @type {String} In form of an alpha code
		 * @example
		 * de, fr, en, ar...
		 */
		this.language = language;
	}
	/**
	 * Records a voice, transforms the input into text and saves it
	 * @return {Promise} Is done when all operations are done
	 */
	startRecording() {
		return new Promise((resolve, reject) => {
			const { location, filename } = config.get('Report');
			const filePath = location + filename;
			let index = 0;

			if (fs.existsSync(filePath)) {
				let contents = fs.readFileSync(filePath);
				let jsonContent = JSON.parse(contents);
				index = jsonContent.results.length;
			}

			const recordingOptions = this.getAudioData.bind(this)(index);
			const startRecord = new Record();
			startRecord.recordInput(recordingOptions)
				.then((id) => {
					this.reportAudio.bind(this)(id)
						.then((data) => {
							this.convertSpeech.bind(this)(data)
								.then(resolve)
								.catch(reject);
						})
						.catch(reject);
				})
				.catch(reject);
		});
	}
	/**
	 * Retrieves all needed configs for the recording
	 * @param  {Number} index - Unique id for the record
	 * @return {Object}       - Each key is an indication
	 */
	getAudioData(index) {
		const {
			location,
			filename,
			sampleRate,
			maxRecordingTime,
			temp
		} = config.get('Audio');

		const id = index;
		const recordingDate = new Date();

		return {
			filename: filename + id,
			id, location, sampleRate,
			maxRecordingTime, recordingDate, temp
		};

	}
	/**
	 * Creates an audio report
	 * @param  {[Number} index - Id of the report
	 * @return {Promise}       - Is done when the report is written
	 */
	reportAudio(index) {
		return new Promise((resolve, reject) => {
			const data = this.getAudioData.bind(this)(index);
			const newReport = new Report(data);
			const { location, filename } = config.get('Report');
			const filePath = location + filename;

			if (!fs.existsSync(filePath)) {
				newReport.writeJSON()
					.then(() => resolve(data))
					.catch((writeError) => {
						reject(writeError);
					});
			} else {
				newReport.extendJSON()
					.then(() => {
						resolve(data);
					})
					.catch((writeError) => {
						reject(writeError);
					});
			}
		});
	}
	/**
	 * Converts the audio input into text
	 * @param  {[type]} audioData [description]
	 * @return {[type]}           [description]
	 */
	convertSpeech(audioData) {
		return new Promise((resolve, reject) => {
			const { azure } = config.get('APIKeys');
			const { location, filename } = config.get('Convert');
			const pathToText = `${location}${filename}`;
			const pathToAudio = `${audioData.location}${audioData.filename}.wav`;

			let speechToText = new SpeechToText();

			speechToText.getAccessToken('azureApp', azure, (accessTokenError, accessToken) => {
				if (accessTokenError) {
					reject(accessTokenError);
				}

				speechToText.convert(pathToAudio, accessToken, this.language, (speechToTextError, res) => {
					if (speechToTextError || !res) {
						if (!res) {
							reject(new Error('Speech to text failed'));
							return;
						}
						reject(speechToTextError);
					}

					let textOutput = res.results[0].lexical; // result of written text from audio
					let jsonToSave = {};
					let resultValue = {
						language: this.language,
						output: textOutput
					};
					let convertFile;
					if (!fs.existsSync(pathToText)) {
						convertFile = JSON.parse(fs.readFileSync(pathToText));
						jsonToSave = convertFile.texts[this.name] = resultValue;
					} else {
						jsonToSave[this.name] = resultValue;
						jsonToSave = { texts: jsonToSave };
					}
					fs.writeFile(pathToText, JSON.stringify(jsonToSave), (writeError) => {
						if (writeError) {
							reject(writeError);
						}
						resolve();
					});
				});
			});
		});
	}
}

import fs from 'fs';
import logUtil from '../../../../utils/logUtil';
import rec from 'node-record-lpcm16';

/**
 * @class Handling the audio recording, specially the wav files
 */
export default class Record {
	/**
	 * Records the input in a wav file
	 * @param  {Object} options - The recording options
	 * @return {Promise}        - Is called when recorded and written in a file
	 */
	recordInput(options) {
		return new Promise((resolve, reject) => {
			const {
				location,
				filename,
				id,
				sampleRate,
				maxRecordingTime
			} = options;

			const file = fs.createWriteStream(
				`${location}${filename}.wav`, {
					encoding: 'binary'
				});
			rec.start({ sampleRate, verbose: true });

			const timeout = setTimeout(() => {
				rec.stop().pipe(file, (recordingErr) => {
					logUtil.log({
						type: 'error',
						title: 'Recording failed',
						messages: [
							{ 'Original error': recordingErr }
						]
					});
					reject();
				});
				clearTimeout(timeout);
				resolve(id);
			}, maxRecordingTime);
		});
