import config from 'config';
import fs from 'fs';
import Record from './utils/records';
import Report from './utils/reports';

/**
 * @class Manages the Recording
 */
export default class Microphone {
	/**
	 * Writes a report file for the audi recoring
	 * @param  {Number} index
	 * @private
	 */
	reportAudio(index) {
		const { location, filename } = config.get('Report');
		const data = this.getAudioData.bind(this)(index);
		const newReport = new Report(location, filename, data);

		if (!fs.existsSync(location + filename)) {
			newReport.writeJSON();
		} else {
			newReport.extendJSON();
		}
	}
	/**
	 * Retrieves the relevant audio report
	 * @param  {Number} index
	 * @private
	 */
	getAudioData(index) {
		const {
			location,
			filename,
			sampleRate,
			maxRecordingTime
		} = config.get('Audio');

		const id = index;
		const recordingDate = new Date();

		return { filename, id, location, sampleRate,
			maxRecordingTime, recordingDate };
	}
	/**
	 * Start recording
	 * @param  {Function} callback
	 * @public
	 */
	startRecording(callback) {
		const { location, filename } = config.get('Report');
		const filePath = location + filename;
		let index = 0;

		if (fs.existsSync(filePath)) {
			let contents = fs.readFileSync(filePath);
			let jsonContent = JSON.parse(contents);

			index = jsonContent.results.length;
		}

		const recordingOptions = this.getAudioData(index);
		recordingOptions.language = 'german';

		const startRecord = new Record(recordingOptions, this.reportAudio.bind(this));
	}
}

import fs from 'fs';
import rec from 'node-record-lpcm16';

/**
 * @class Manages the audio recording
 */
export default class Record {
	/**
	 * Records and writes the audio in a wav file
	 * @param  {Object} options
	 * @param  {Function} callback
	 */
	constructor(options, callback) {
		const {
			location,
			filename,
			id,
			sampleRate
		} = options;

		const file = fs.createWriteStream(
			`${location}${filename}${id}.wav`, {
				encoding: 'binary'
			});
		rec.start({ sampleRate, verbose: true });

		setTimeout(() => {
			rec.stop().pipe(file);
			callback();
		}, options.maxRecordingTime);
	}
}

import fs from 'fs';
import logUtil from '../../../../utils/logUtil';

/**
 * @class Manages the audio reports
 */
export default class CreateReport {
	/**
	 * @param  {String} location
	 * @param  {String} filename
	 * @param  {Object} data
	 */
	constructor(location, filename, data) {
		this.writeJSON.bind(this);
		this.extendJSON.bind(this);

		/**
		 * Audio data
		 * @type {Object}
		 */
		this.data = data;
		/**
		 * Complete file path
		 * @type {String}
		 */
		this.filePath = `${location}${filename}`;

		/**
		 * New audio report config
		 * @type {Object}
		 */
		this.newObject = { results: [] };
		this.newObject.results.push(data);

		logUtil.log({
			type: 'info',
			title: 'Audio report initialized'
		});
	}
	/**
	 * Writes an audio report in a json file
	 * @public
	 */
	writeJSON() {
		fs.writeFileSync(this.filePath, JSON.stringify(this.newObject));
		logUtil.log({
			type: 'info',
			title: 'New audio JSON-file written'
		});
	}
	/**
	 * Extends the audio report in an existing json file
	 * @public
	 */
	extendJSON() {
		let contents = fs.readFileSync(this.filePath);
		let jsonContent = JSON.parse(contents);

		jsonContent.results.push(this.data);
		fs.writeFileSync(this.filePath, JSON.stringify(jsonContent));
		logUtil.log({
			type: 'info',
			title: 'Audio JSON-file extended'
		});
	}
}

import { Servo } from 'johnny-five';
import { MOTOR_CONSTANTS } from '../../constants';
const {
	LIFT_PIN,
	LIFT_TYPE,
	TILT_PIN,
	TILT_TYPE,
	SPEED,
	LYING,
	STANDING,
	TILTED,
	STRAIGHT
} = MOTOR_CONSTANTS;

/**
 * @class Manages the servor motors
 */
export default class Motor {
	/**
	 * Constructor for the Motor class. Needs a j5 board instance
	 * to work well. !CAUTION¡ The j5 board must already be ready.
	 * @param  {Johnny Five board instance} board
	 * @private
	 */
	constructor(board) {
		/**
		 * The servo meant to lift the box up and down
		 * @type {Servo}
		 */
		this.liftServo = new Servo({
			pin: LIFT_PIN,
			startAt: LYING,
			type: LIFT_TYPE,
			board
		});
		/**
		 * The servo meant to make the box look up and straight
		 * @type {Servo}
		 */
		this.tiltServo = new Servo({
			pin: TILT_PIN,
			startAt: STRAIGHT,
			type: TILT_TYPE,
			board
		});

		/**
		 * Is the Box standing up?
		 * @type {Boolean}
		 */
		this.stands = false;
		/**
		 * Is the Box looking up?
		 * @type {Boolean}
		 */
		this.titls = false;
	}
	/**
	 * Generic function that checks if a servo is already in the
	 * whished position and rotates it if not.
	 * @param  {Boolean} actualPositionState - The actual state to check
	 * @param  {Boolean} whishedPositionState - The whished state
	 * @param  {Number} positionToMoveTo - The rotation the servo need to accomplish
	 * @param  {Johnny Five Servo instance} servo
	 * @return {Promise}
	 * @private
	 */
	moveServo({
		actualPositionState,
		whishedPositionState,
		positionToMoveTo,
		servo }) {
		return new Promise((resolve) => {
			if (actualPositionState === whishedPositionState) {
				resolve();
				return;
			}
			servo('move:complete', resolve);
			servo.to(positionToMoveTo, SPEED);
			servo('move:complete', false);
		});
	}
	/**
	 * Lifts the Box down
	 * @return {Promise}
	 * @public
	 */
	lieDown() {
		return this.checkAndMove({
			actualPositionState: this.stands,
			whishedPositionState: false,
			positionToMoveTo: LYING,
			servo: this.liftServo
		});
	}
	/**
	 * Lifts the Box up
	 * @return {Promise}
	 * @public
	 */
	standUp() {
		return this.checkAndMove({
			actualPositionState: this.stands,
			whishedPositionState: true,
			positionToMoveTo: STANDING,
			servo: this.liftServo
		});
	}
	/**
	 * Makes the Box look up
	 * @return {Promise}
	 * @public
	 */
	lookUp() {
		return this.checkAndMove({
			actualPositionState: this.titls,
			whishedPositionState: true,
			positionToMoveTo: TILTED,
			servo: this.liftServo
		});
	}
	/**
	 * Makes the Box look down
	 * @return {Promise}
	 * @public
	 */
	lookStraight() {
		return this.checkAndMove({
			actualPositionState: this.thils,
			whishedPositionState: false,
			positionToMoveTo: STRAIGHT,
			servo: this.liftServo
		});
	}
}

