import config from 'config';
import fs from 'fs';
import Record from './utils/records';
import Report from './utils/reports';

export default class Translator {
	constructor() {
		this.reportAudio = this.reportAudio.bind(this);
		this.getAudioData = this.getAudioData.bind(this);
		this.startRecording = this.startRecording.bind(this);
	}
	reportAudio(index) {
		const { location, filename } = config.get('Report');
		const data = this.getAudioData.bind(this)(index);
		const newReport = new Report(location, filename, data);

		if (!fs.existsSync(location + filename)) {
			newReport.writeJSON();
		} else {
			newReport.extendJSON();
		}
	}
	getAudioData(index) {
		const {
			location,
			filename,
			sampleRate,
			maxRecordingTime
		} = config.get('Audio');

		const id = index;
		const recordingDate = new Date();

		return { filename, id, location, sampleRate,
			maxRecordingTime, recordingDate };
	}
	startRecording(callback) {
		const { location, filename } = config.get('Report');
		const filePath = location + filename;
		let index = 0;

		if (fs.existsSync(filePath)) {
			let contents = fs.readFileSync(filePath);
			let jsonContent = JSON.parse(contents);

			index = jsonContent.results.length;
		}

		const recordingOptions = this.getAudioData(index);
		recordingOptions.language = 'german';

		const startRecord = new Record(recordingOptions, this.reportAudio.bind(this));
	}
}

import fs from 'fs';
import rec from 'node-record-lpcm16';

export default class Record {
	constructor(options, callback) {
		const {
			location,
			filename,
			id,
			sampleRate
		} = options;

		const file = fs.createWriteStream(
			`${location}${filename}${id}.wav`, {
				encoding: 'binary'
			});
		rec.start({ sampleRate, verbose: true });

		setTimeout(() => {
			rec.stop().pipe(file);
			callback();
		}, options.maxRecordingTime);
	}
}

import fs from 'fs';
import logUtil from '../../../../utils/logUtil';

export default class CreateReport {
	constructor(location, filename, data) {
		this.writeJSON.bind(this);
		this.extendJSON.bind(this);

		this.data = data;
		this.filePath = `${location}${filename}`;

		this.newObject = { results: [] };
		this.newObject.results.push(data);

		logUtil.log({
			type: 'info',
			title: 'Audio report initialized'
		});
	}
	writeJSON() {
		fs.writeFileSync(this.filePath, JSON.stringify(this.newObject));
		logUtil.log({
			type: 'info',
			title: 'New audio JSON-file written'
		});
	}
	extendJSON() {
		let contents = fs.readFileSync(this.filePath);
		let jsonContent = JSON.parse(contents);

		jsonContent.results.push(this.data);
		fs.writeFileSync(this.filePath, JSON.stringify(jsonContent));
		logUtil.log({
			type: 'info',
			title: 'Audio JSON-file extended'
		});
	}
}

import { Servo } from 'johnny-five';
import { MOTOR_CONSTANTS } from '../../constants';
const {
	LIFT_PIN,
	LIFT_TYPE,
	TILT_PIN,
	TILT_TYPE,
	SPEED,
	LYING,
	STANDING,
	TILTED,
	STRAIGHT
} = MOTOR_CONSTANTS;

export default class Motor {
	constructor(board) {
		this.liftServo = new Servo({
			pin: LIFT_PIN,
			startAt: LYING,
			type: LIFT_TYPE,
			board
		});
		this.tiltServo = new Servo({
			pin: TILT_PIN,
			startAt: STRAIGHT,
			type: TILT_TYPE,
			board
		});

		this.stands = false;
		this.titls = false;
	}
	checkAndMove(tocheck, checkValue, position) {
		return new Promise((resolve) => {
			if (tocheck === checkValue) {
				resolve();
				return;
			}
			this.liftServo('move:complete', resolve);
			this.liftServo.to(position, SPEED);
			this.liftServo('move:complete', false);
		});
	}
	lieDown() {
		return this.checkAndMove(this.stands, false, LYING);
	}
	standUp() {
		return this.checkAndMove(this.stands, true, STANDING);
	}
	lookUp() {
		return this.checkAndMove(this.titls, true, TILTED);
	}
	lookStraight() {
		return this.checkAndMove(this.thils, false, STRAIGHT);
	}
}

