import config from 'config';
import { Boards } from 'johnny-five';
import Box from './components/box';
import { createBoard, getBoardInBoardsByName } from './utils/boardsUtil';
import { createClient, getClientInClientsByName } from './utils/clientsUtil';
import logUtil from './utils/logUtil';

/**
 * @class Most global class
 * @param none
 */
export default class BoardsChain {
	constructor() {
		const boardsConfigs = config.get('boards');
		const boards = new Boards(boardsConfigs.map((board) =>
			createBoard(board.name, board.id)));

		this.boxes = [];
		boards.each((board, index) => {
			let next;
			let prev;
			let isMaster = false;

			if (index === 0) {
				isMaster = true;
				prev = boardsConfigs[index + 1];
			} else {
				prev = boardsConfigs[index - 1];
			}

			if (index === boardsConfigs.length - 1) {
				next = boardsConfigs[0];
			} else {
				next = boardsConfigs[index + 1];
			}

			if (boardsConfigs.length === 1) {
				prev = next = board;
			}

			logUtil.log({
				type: 'info',
				title: `Box "${board.id}" initialized`,
				messages: [
					{ prev: prev.name },
					{ next: next.name },
					{ isMaster }
				]
			});
			const client = createClient(board.id);
			this.boxes.push(new Box(board, client, { next, isMaster, prev }));
		});

		this.masterBox = this.boxes[0];
		boards.on('ready', this.onAllBoardsReady.bind(this));
	}
	onAllBoardsReady() {
		logUtil.log({
			type: 'hardware',
			title: `All boards ready`
		});
		this.masterBox.startTheShow();
	}
}

const chain = new BoardsChain();

import config from 'config';
import Particle from 'particle-io';

const token = config.get(`token`);

module.exports = {
	createBoard: (id, deviceId) => {
		return { id, io: new Particle({ token, deviceId, deviceName: id }) };
	},
	getBoardInBoardsByName: (boardsArray, name) => {
		let relevantBoard;
		boardsArray.each((board) => {
			if (board.id === name) {
				relevantBoard = board;
			}
		});
		return relevantBoard;
	}
};

import config from 'config';
import { connect } from 'mqtt';
const { usr, psw } = config.get('shiftr');
const url = `mqtt://${usr}:${psw}@broker.shiftr.io`;

module.exports = {
	createClient: (name) => {
		const client = connect(url, { clientId: name });
		client.name = name;
		return client;
	},
	getClientInClientsByName: (clientsArray, name) =>
		clientsArray.filter((client) =>
			client.name === name)[0]
};

import chalk from 'chalk';

const PREFIX_TABULATION = `
		   `; // Do not refactor. Intended line break and spaces

const getFormattedTime = () => {
	const time = new Date();
	const timeFormatted = ('0' + time.getHours()).slice(-2) +
		':' + ('0' + time.getMinutes()).slice(-2) +
		':' + ('0' + time.getSeconds()).slice(-2);
	return chalk.white('[') +
		chalk.gray(timeFormatted) +
		chalk.white('] ');
};

const getColorByType = (type) => {
	switch (type) {
		case 'info':
			return 'blue';
		case 'hardware':
			return 'magenta';
		case 'shiftr':
			return 'green';
		case 'error':
			return 'red';
		case 'warning':
			return 'orange';
		case 'discrete':
			return 'gray';
		default:
			return 'white';
	}
};

const doLog = (options) => {
	const { type, title, messages } = options;
	const color = getColorByType(type);
	const toLog = [ '\n' + getFormattedTime() ];
	if (title && typeof title === 'string') {
		toLog.push(chalk.inverse.bold[ color ](' ' + title + ' '));
	}
	messages.forEach((message) => {
		let formattedMessage = chalk[ color ](PREFIX_TABULATION, message);
		if (typeof message === 'object') {
			let key = Object.keys(message)[0];
			formattedMessage = chalk[ color ](PREFIX_TABULATION,
				' ' + chalk.underline(key + ':'), message[key]);
		}
		toLog.push(formattedMessage);
	});
	toLog.push('\n');
	console.log.apply(console, toLog);
};

module.exports = {
	log: (messageOrOptions) => {
		const { type, title, messages } = messageOrOptions;
		if (typeof messageOrOptions === 'object' &&
			(type || title || messages)) {
			const options = Object.assign({}, {
				type: false,
				title: false,
				messages: []
			}, messageOrOptions);
			doLog(options);
		} else {
			doLog({
				type: false,
				title: false,
				messages: messageOrOptions
			});
		}
	}
};
